mapper.update:
update user
set password = #{password}
where id in

    #{id}

mapper.select:
SELECT * FROM lostfound


        AND (title LIKE CONCAT('%', #{keyword}, '%') OR place LIKE CONCAT('%', #{keyword}, '%') or phone LIKE CONCAT('%', #{keyword}, '%'))


        AND type = #{type}


        AND state = #{state}


ORDER BY pub_date DESC
LIMIT #{offset}, #{pageSize}

SELECT COUNT(*)
FROM lostfound


        AND type = #{type}
